mapper.select:
SELECT EXISTS(
SELECT 1 FROM user
WHERE user_login_id = #{loginId}
)

mapper.update:
UPDATE user
SET user_refresh_token = #{refreshToken}
WHERE user_id = #{userId}

mapper.select:
SELECT EXISTS(
SELECT 1 FROM user
WHERE user_id = #{userId}
AND user_refresh_token = #{refreshToken}
)

mapper.update:
UPDATE user
SET user_refresh_token = NULL
WHERE user_id = #{userId}
